mapper.select:
select cid, tid, cname, cost, location, description, count
from course
where cid = #{cid,jdbcType=INTEGER}

SELECT c.*, if(a.sid is not null, 1, 0) AS havePaid FROM (SELECT course.*, coach.coach_name AS coachName FROM course, coach where course.tid = coach.tid
AND (coach.coach_name like concat('%', #{keyword}, '%') OR course.cname like concat('%', #{keyword}, '%'))
) AS c LEFT JOIN apply a ON c.cid = a.cid AND a.sid = #{sid}

mapper.insert:
insert into comment (
    cid,
    sid,
    context
) values (
    #{cid},
    #{sid},
    #{context}
)

mapper.update:
update course
set count = #{count}
where cid = #{cid}
